def main(data):
    #data = request.get_json()
    url = data.get('URL')
    asin = data.get("ASIN")
    index = pinecone.Index("gptprime")
    if checkForAsin(asin=asin) is False:
        vectors = format_qa(asin)
        vectors.extend(format_reviews(url, asin))
        vectors.append(format_product(url))

        index.upsert(vectors)

    return {}
